import "../../css/modals.css"
import { animated as a } from "react-spring";

const FloatModal = ({visible}) => {
    return (
        !visible ? (
            <div className="modal-wrapper" style={{display: "none"}}>
                <div className="modal-content">
                    <button className="modal-close-btn">X CLOSE</button>
                    {props.children}
                </div>
            </div>
        ) : (
            <div className="modal-wrapper">
                <a.div className="modal-content">
                    <button className="modal-close-btn" onClick={() => props.update()}>X CLOSE</button>
                    {props.children}
                </a.div>
            </div>
        )
    )
}

export default FloatModal;
