import FloatModal from "../modalbase"
import { useRef, useContext } from "react"
import { LoginModalContext } from "../../app/app"
import { GoogleAuthProvider, updateProfile } from "firebase/auth"
import { Auth, createUser, signInWithGoogle } from "../../../firebase"

const LoginFormModal = () => {
    const pass_field = useRef("")
    const name_field = useRef("")
    const email_field = useRef("")
    const repass_field = useRef("")
    const {visible, setVisible} = useContext(LoginModalContext)
    const login_google = () => {
        let auth_google = new GoogleAuthProvider
        signInWithGoogle(Auth, auth_google).then(()=>{
            setVisible(false)
        }).catch((error) => console.log(error.message))
    }
    const login_pass_email = () => {
        createUser(Auth, email_field.current.value, pass_field.current.value).then(() => {
            setVisible(false)
        }).catch((error) => console.log(error.message)).finally(() => {
            updateProfile(Auth.currentUser, {displayName: name_field.current.value, photoURL: null})
        })
    }
    return (
        <FloatModal visible={visible} close={() => setVisible(false)}>
            <div className="login-form-wrapper">
                <div className="login-form-top">
                    <form action="" onSubmit={login_pass_email}>
                        <div className="login-form-wrap-input">
                            <label for>Username:</label>
                            <input className="login-form-input" ref={name_field} type="text" required placeholder="Enter your username..."/>
                        </div>
                        <div className="login-form-wrap-input">
                            <label for>Email:</label>
                            <input className="login-form-input" ref={email_field} type="email" placeholder="Enter your email address..."required />
                        </div>
                        <div className="login-form-wrap-input">
                            <label for="pass">Password:</label>
                            <input id="pass" className="login-form-input" ref={pass_field} type="password" placeholder="Enter your password..."required />
                        </div>
                        <div className="login-form-wrap-input">
                            <label for>Confirm password:</label>
                            <input id="rep" className="login-form-input" ref={repass_field} type="password" placeholder="Confirm your password..."required />
                        </div>
                        <button className="login-form-btn">Login</button>
                    </form>
                </div>
                <div className="login-form-bottom">
                    <button onClick={login_google}>Login with Google</button>
                </div>
            </div>
        </FloatModal>
    )
}

export default LoginFormModal;
