import "../../css/modals.css"
import { useSpring, animated as a } from "react-spring";

const FloatModal = (props) => {
    const exit_method = () => {
        if (window.confirm("Are you sure you wanna close the window? Fields are gonna be reseted!")) {
            props.update()
        }
    }
    const [closeAnim, setCloseAnim] = useState(false)
    const contentProps = useSpring({
        opacity: closeAnim ? 0 : 1,
        marginTop: closeAnim ? -500 : 0
    });
    return (
        !props.visible ? (
            <div className="modal-wrapper" style={{display: "none"}}>
                <div className="modal-content">
                    <button className="modal-close-btn">X CLOSE</button>
                    {props.children}
                </div>
            </div>
        ) : (
            <div className="modal-wrapper">
                <a.div className="modal-content" style={contentProps}>
                    <button className="modal-close-btn" onClick={exit_method}>X CLOSE</button>
                    {props.children}
                </a.div>
            </div>
        )
    )
}

export default FloatModal;
