import {Auth} from "./firebase"
import React, { useEffect } from 'react';
import io from "socket.io-client"
import ReactDOM from 'react-dom/client';
import App from './components/app/app';
import { checkActionCode } from "firebase/auth";
import {Route, BrowserRouter, Routes, useNavigate, NavLink} from "react-router-dom"

let ClientID;
const Client = io("http://localhost:5000")
Client.on("socket-id", ({ID}) => {
  ClientID = ID
})
const root = ReactDOM.createRoot(document.getElementById('root'))
const Reset_root = () => {
   let error, code;
   const Comp = (props) => {
      return (
        <div>
          Redirecting...
          {props.children}
        </div>
      )
   }
   useEffect(() => {
      const queryParams = new URLSearchParams(window.location.search)
      const oobCode = queryParams.get("oobCode")
      const apiKey = queryParams.get("apiKey")
      if (oobCode != null && apiKey != null) {
        checkActionCode(Auth, oobCode).then(() => {
          code = oobCode
          error = null
        }).catch((err) => {
          code = null
          error = err.code
        })
      }
   }, [])
   const state = {
      code: code,
      error: error
   }
   let nav = useNavigate()
   return (
      <Comp>
        {
          nav("/")
        }
      </Comp>
    )
}
const Main_root = () => {
  return (
    <BrowserRouter>
      <Routes>
          <Route index element={<App></App>}></Route>
          <Route path="/resetPassword" element={<Reset_root></Reset_root>}></Route>
      </Routes>
    </BrowserRouter>
  )
}
root.render(
  <React.StrictMode>
      <Main_root />
  </React.StrictMode>
)
export default {Client: Client, CID: ClientID};
