import FloatModal from "../modalbase"
import index from "../../../index"

const Add_ticket_modal = (props) => {
    const form_submit_ticket = (e) => {
        e.preventDefault();
        index.Client.emit("ticket-submit", {table: 1, desc: document.getElementById("desc-field").value});
    }
    // ADD THE TABLES
    const tables = []
    for (let i = 0; i <= 30; i++) {
            tables.push(<option> Table {i} </option>)
    }
    return (
        <FloatModal {...props}>
            <div class="add-ticket-wrapper">
                <p>New ticket information</p>
                <form autoComplete="false" onSubmit={form_submit_ticket}>
                    <select>
                        {tables}
                    </select>
                    <textarea id="desc-field" type="text" maxLength={200} min={10} rows={10} placeholder="Description of the ticket..." style={{resize:"none"}} required></textarea>
                    <input type="submit" class="form-submit-btn" value="SUBMIT"></input>
                </form>
            </div>
        </FloatModal>
    )
}

export default Add_ticket_modal
