import { useContext } from "react";
import { InfoModalContext, InfoMsgModalContext } from "../../app/app";
import { useSpring, animated as a } from "react-spring";

export const DisplayInfoMsg = (msgs) => {
    const {visible, setVisible} = useContext(InfoModalContext)
    const {setMsg} = useContext(InfoMsgModalContext)
    setVisible(true)
    setMsg(msgs)
    console.log("a")
}

const InfoModal = () => {
    const {visible} = useContext(InfoModalContext)
    const {msg} = useContext(InfoMsgModalContext)
    const show_style = useSpring({
        bottom: visible ? "-10px" : "-100px"
    })
    const hide_style = useSpring({
        bottom: !visible ? "-100px" : "-10px"
    })
    console.log(visible)
    return (
        visible ? (
            <a.div className="info-wrapper" style={show_style}>
                <div className="info-content">
                    <p className="info-msg">{msg}</p>
                </div>
            </a.div>
        ) : (
            <a.div className="info-wrapper" style={hide_style}>
                <div className="info-content">
                    <p className="info-msg">{msg}</p>
                </div>
            </a.div>
        )
    )
}

export default InfoModal;
