import FloatModal from "../modalbase"
import Client from "../../../index"
import { titleCase } from "./renewpassword"
import loading from "../../../resc/loading.jpg"
import { AddModalContext } from "../../app/app"
import { useEffect, useRef, useContext, useState } from "react"

const AddTicketModal = () => {
    const desc_field = useRef("")
    const [error, setError] = useState(false)
    const [errorMsg, setErrorMsg] = useState("")
    const [loadingLogin, setLoading] = useState(false)
    const {visible, setVisible} = useContext(AddModalContext)
    const {setMsg} = useContext(InfoMsgModalContext)
    const {visibleI, setVisibleI} = useContext(InfoModalContext)
    const show_info = (msgs, seconds) => {
        if (visibleI) {
            timeout.forEach((task) => {
                clearTimeout(task)
                timeout.pop()
            })
        }
        setMsg(msgs)
        setVisibleI(true)
        let t = setTimeout(() => {
            setVisibleI(false)
        }, seconds * 1000)
        timeout.push(t)
    }
    const clear_fields = () => {
        setError(false)
        setLoading(false)
        setErrorMsg(false)
        desc_field.current.value = ""
    }
    useEffect(() => {
        Client.on("ticket-exists", ({msg}) => {
            setError(true)
            setErrorMsg("❌ Error: " + titleCase(msg))
        })
        Client.on("ticket-close-modal", () => {
            setVisible(false)
            clear_fields()
        })
    }, [])
    const form_submit_ticket = (e) => {
        e.preventDefault();
        const o_el = document.getElementById("select-field");
        Client.emit("ticket-submit", {table: o_el.options[o_el.selectedIndex].text, desc: desc_field.current.value})
    }
    const tables = []
    for (let i = 0; i <= 40; i++) {
        tables.push(<option key={i} value={i}> Ticket #{i} </option>)
    }
    return (
        <FloatModal visible={visible} close={() => setVisible(false)}>
            <div className="form-wrapper">
                <div className="form-section-top">
                    <div className="form-bg">
                        <div className="img-holder">
                            {loadingLogin && <img src={loading} alt=""></img>}
                        </div>
                        <p className="form-title">Add new ticket Form</p>
                        {error && <div className="form-error-wrapper">
                                <p className="form-error">{errorMsg}</p>
                            </div>
                        }
                    </div>
                </div>
                <div className="form-section-bottom">
                    <form className="form" action="" autoComplete="false" onSubmit={form_submit_ticket}>
                        <select id="select-field" className="select-field">
                            {tables}
                        </select>
                        <textarea ref={desc_field} id="desc-field" type="text" maxLength={200} min={10} rows={10} placeholder="Description of the ticket..." style={{resize:"none"}} required></textarea>
                        <input id="submit-field" type="submit" className="form-submit-btn" value="SUBMIT"></input>
                    </form>
                </div>
            </div>
        </FloatModal>
    )
}

export default AddTicketModal;
