import "../../css/tickets.css"
import index from "../../index"
import { useState } from "react";
import { useSpring, animated as a } from "react-spring";

const Ticket = (props) => {
    const [deleteAnim, setDeleteAnim] = useState(false)
    const contentProps = useSpring({
        opacity: deleteAnim ? 0 : 1,
        marginTop: deleteAnim ? -300 : 0
    });
    const trigger_delete_anim = () => {
        setDeleteAnim(true)
    }
    const delete_ticket = () => {
        index.Client.volatile.emit("ticket-delete-anim", {table: props.name})
        setTimeout(()=> index.Client.volatile.emit("ticket-delete", {table: props.name}), 1500)
    }
    return (
        <a.div id={props.name} className="ticket-wrapper" style={contentProps}>
            <div className="ticket-content">
                <p className="ticket-table">{props.name}</p>
                <p className="ticket-desc">{props.desc}</p>
                <button className="ticket-complete" onClick={delete_ticket}>COMPLETE</button>
            </div>
        </a.div>
    )
}

export default {Ticket, trigger_delete_anim};
