import FloatModal from "../modalbase"
import { useRef } from "react"
import {Auth, createUser, signInWithGoogle} from "../../../firebase"
import {sendSignInLinkToEmail, isSignInWithEmailLink, sendEmailVerification} from "firebase/auth"

const LoginForm = (props) => {
    const email_field = useRef("")
    const pass_field = useRef("")
    const submit_req = (e) => {
        e.preventDefault()
        createUser(Auth, email_field.current, pass_field.current).then(()=>{
            props.update()
            sendSignInLinkToEmail(Auth, email_field.current).then()
        }).catch((error) => console.log(error.message))
    }
    return (
        <FloatModal {...props}>
            <div>
                <div>
                    <input ref={email_field} type="email"></input>
                    <input ref={pass_field} type="password"/>
                    <input type="submit" onClick={submit_req} value="Signup"></input>
                </div>
                <button>Signup with Google</button>
            </div>
        </FloatModal>
    )
}

export default LoginForm;
