import "../../css/tickets.css"
import index from "../../index"

const Ticket = (props) => {
    const delete_ticket = () => {
        //set timer to 5 seconds, after that, load the magic animation
        setTimeout(() =>{
            const selector = document.querySelector('.yourdiv')
            selector.classList.add('magictime', 'puffIn')
        }, 5000)
        //index.Client.emit("ticket-delete", {table: props.name})
    }
    return (
        <div className="ticket-wrapper">
            <div className="ticket-content">
                <p className="ticket-table">{props.name}</p>
                <p className="ticket-desc">{props.desc}</p>
                <button className="ticket-complete" onClick={delete_ticket}>COMPLETE</button>
            </div>
        </div>
    )
}

export default Ticket;
