import "../../css/modals.css"
import { animated as a, useSpring } from "react-spring";

const FloatModal = (props) => {
    const styles = useSpring({ 
        opacity: props.visible ? 1 : 0,
        top: props.visible ? 300 : 0
    })
    return (
        !props.visible ? (
            <div className="modal-wrapper" style={{display: "none"}}>
            </div>
        ) : (
            <div className="modal-wrapper">
                <a.div className="modal-content" style={styles}>
                    <button className="modal-close-btn" onClick={() => props.close()}>X CLOSE</button>
                    {props.children}
                </a.div>
            </div>
        )
    )
}

export default FloatModal;
