import FloatModal from "../modalbase"
import { Auth } from "../../../firebase"
import loading from "../../../resc/loading.jpg"
import { oobcode } from "../../head/header"
import { useRef, useContext, useState } from "react"
import { confirmPasswordReset } from "firebase/auth"
import { timeout, PassModalContext, InfoModalContext, InfoMsgModalContext } from "../../app/app"

export function titleCase(string){
    return (string[0].toUpperCase() + string.slice(1).toLowerCase());
}

const RenewPassFormModal = (props) => {
    const pass_field = useRef("")
    const repass_field = useRef("")
    const [error, setError] = useState(false)
    const [errorMsg, setErrorMsg] = useState("")
    const {setMsg} = useContext(InfoMsgModalContext)
    const [loadingLogin, setLoading] = useState(false)
    const {visibleP, setVisibleP} = useContext(PassModalContext)
    const {visibleI, setVisibleI} = useContext(InfoModalContext)
    const show_info = (msgs, seconds) => {
        if (visibleI) {
            timeout.forEach((task) => {
                clearTimeout(task)
                timeout.pop()
            })
        }
        setMsg(msgs)
        setVisibleI(true)
        let t = setTimeout(() => {
            setVisibleI(false)
        }, seconds * 1000)
        timeout.push(t)
    }
    const clear_fields = () => {
        setError(false)
        setLoading(false)
        setErrorMsg(false)
        pass_field.current.value = ""
        repass_field.current.value = ""
    }
    const renew_pass = (ev) => {
        ev.preventDefault()
        setLoading(true)
        if (pass_field.current.value !== repass_field.current.value) {
            setError(true)
            setLoading(false)
            setErrorMsg("❌ Error: Passwords do not match")
            return
        }
        confirmPasswordReset(Auth, props.code, pass_field.current.value).then(() => {
            clear_fields()
            setLoading(false)
            setVisibleP(false)
            show_info("✅ Successfully changed your password for email: " + Auth.currentUser.email, 4)
        }).catch((err) => {
            if (err.code) {
                setError(true)
                setLoading(false)
                setErrorMsg("❌ Error: " + titleCase(err.code.toString().replace("auth/", "").replace("-", " ")))
            }
        })
    }
    return (
        <FloatModal visible={visibleP} close={() => {
            setVisibleP(false)
            clear_fields()
        }}>
            <div className="renewpass-form-wrapper">
                <div className="renewpass-form-section-top">
                    <div className="form-bg">
                        <div className="img-holder">
                            {loadingLogin && <img src={loading} alt=""></img>}
                        </div>
                        <p className="form-title">Renew password Form</p>
                        {error && <div className="form-error-wrapper">
                            <p className="form-error">{errorMsg}</p>
                        </div>
                        }
                    </div>
                </div>
                <div className="renewpass-form-section-bottom">
                    <form className="renewpass-form" onSubmit={renew_pass} autoComplete="off">
                        <p>{Auth.currentUser && Auth.currentUser.email}</p>
                        <div className="renewpass-form-wrap-input">
                            <label htmlFor="pass1">Password:</label>
                            <input id="pass1" className="renewpass-form-input" ref={pass_field} maxLength={45} type="password" placeholder="Enter your new password..." required />
                        </div>
                        <div className="renewpass-form-wrap-input">
                            <label htmlFor="rep1">Confirm password:</label>
                            <input id="rep1" className="renewpass-form-input" ref={repass_field} maxLength={45} type="password" placeholder="Confirm your new password..." required />
                        </div>
                        <button className="renewpass-form-btn">Renew password</button>
                    </form>
                </div>
            </div>
        </FloatModal>
    )
}

export default RenewPassFormModal;
