import "../../css/app.css"
import Header from "../head/header"
import Tickets from "../tickets/tickets"
import Footer from "../footer/footer"
import React, { useState } from "react"
import {Auth} from "../../firebase"
import LoginForm from "../modals/components/login"
import AddTicketModal from "../modals/components/addticket"
import { getAuth, onAuthStateChanged } from "firebase/auth";
export const LoggedContext = React.createContext()

const App = () => {
  const [Logged, setLogged] = useState(false)
  onAuthStateChanged(Auth, (user) => {
    if (user) {
      setLogged(true)
    } else {
      setLogged(false)
    }
  });
  return (
    <LoggedContext.Provider value={
      {
        log: Logged,
        setLog: setLogged
      }
    }>
      <div className="root-wrapper">
          <Header></Header>
          <Tickets></Tickets>
          <Footer></Footer>
          <LoginForm visible={loginModal} update={() => setLoginModal(false)}></LoginForm>
          <Add_ticket_modal visible={addModal} update={() => setAddModal(false)}></Add_ticket_modal>
      </div>
    </LoggedContext.Provider>
  )
}

export default App;
