import "../../css/modals.css"

const FloatModal = (update, visible) => {
    return (
        !visible ? (
            <div className="modal-wrapper" style={{display: "none"}}>
                <div className="modal-content">
                    <button className="modal-close-btn" onClick={() => update}>X CLOSE</button>
                    {props.children}
                </div>
            </div>
        ) : (
            <div className="modal-wrapper">
                <div className="modal-content">
                    <button className="modal-close-btn">X CLOSE</button>
                    {props.children}
                </div>
            </div>
        )
    )
}

export default FloatModal;
