import FloatModal from "../modalbase"
import { useRef, useContext, useState } from "react"
import { LoginModalContext } from "../../app/app"
import { GoogleAuthProvider, updateProfile } from "firebase/auth"
import { Auth, createUser, signInWithGoogle } from "../../../firebase"
import googleicon from "../../../resc/google.png"

const LoginFormModal = () => {
    const pass_field = useRef("")
    const name_field = useRef("")
    const email_field = useRef("")
    const repass_field = useRef("")
    const [error, setError] = useState(true)
    const [errorMsg, setErrorMsg] = useState("")
    const {visible, setVisible} = useContext(LoginModalContext)
    const login_google = () => {
        let auth_google = new GoogleAuthProvider
        signInWithGoogle(Auth, auth_google).then(()=>{
            setVisible(false)
        }).catch((err) => {
            setError(true)
            setErrorMsg("X Error: " + err.code.toString())
        }).finally(() => {
            setError(false)
            setErrorMsg(false)
            name_field.current.value = ""
            email_field.current.value = ""
            pass_field.current.value = ""
            repass_field.current.value = ""
        })
    }
    const login_pass_email = () => {
        createUser(Auth, email_field.current.value, pass_field.current.value).then(() => {
            setVisible(false)
        }).catch((err) => {
            setError(true)
            setErrorMsg("X Error: Couldn't login the user, contact the administrator = [" + err.message + "]")
        }).finally(() => {
            setError(false)
            setErrorMsg(false)
            name_field.current.value = ""
            pass_field.current.value = ""
            repass_field.current.value = ""
            email_field.current.value = ""
            updateProfile(Auth.currentUser, {displayName: name_field.current.value, photoURL: null})
        })
    }
    return (
        <FloatModal visible={visible} close={() => setVisible(false)}>
            <div className="login-form-wrapper">
                <div className="login-form-section-top">
                    <div className="form-bg">
                        <p className="form-title">Login Form</p>
                        <div className="form-error-wrapper">
                            {error && <p className="form-error">{errorMsg}</p>}
                        </div>
                    </div>
                </div>
                <div className="login-form-section-bottom">
                    <form className="login-form" action="" onSubmit={login_pass_email} autoComplete="off">
                            <div className="login-form-wrap-input">
                                <label htmlFor="user">Username:</label>
                                <input id="user" className="login-form-input" ref={name_field} min={3} maxLength={45} type="text" required placeholder="Enter your username..."/>
                            </div>
                            <div className="login-form-wrap-input">
                                <label htmlFor="email">Email:</label>
                                <input id="email" className="login-form-input" ref={email_field} maxLength={100} type="email" placeholder="Enter your email address..." required />
                            </div>
                            <div className="login-form-wrap-input">
                                <label htmlFor="pass">Password:</label>
                                <input id="pass" className="login-form-input" ref={pass_field} maxLength={45} type="password" placeholder="Enter your password..." required />
                            </div>
                            <div className="login-form-wrap-input">
                                <label htmlFor="rep">Confirm password:</label>
                                <input id="rep" className="login-form-input" ref={repass_field} maxLength={45} type="password" placeholder="Confirm your password..." required />
                            </div>
                            <button className="login-form-btn">Login</button>
                    </form>
                    <div className="login-form-bottom">
                        <button onClick={login_google}>
                            <img src={googleicon}></img>
                            Login with Google
                        </button>
                    </div>
                </div>
            </div>
        </FloatModal>
    )
}

export default LoginFormModal;
