import "../../css/tickets.css"
import Ticket from "./ticket"
import index from "../../index"
import { useState, useEffect } from "react"
import noresult from "../../resc/no-result.gif"
import BlobTRight from "../../resc/BlobTRight.png"
import Add_ticket_modal from "../modals/components/add_ticket"

const Tickets = () => {
    const [tickets, setTickets] = useState([]);
    const [addModal, setAddModal] = useState(false);
    const tickets_map = [{}]
    ////////////////////////
    const show_modal = () => setAddModal(true)
    const hide_modal = () => setAddModal(false)
    useEffect(() => {
        index.Client.on("tickets", ({tickets_list}) => {
            setTickets(tickets_list)
        })
        index.Client.on("ticket-delete-anim", ({tickets_list}) => {
            tickets_map((K, V) => {
                
            })
        })
    },[])
    return (
        <div className="tickets-wrapper">
            <Add_ticket_modal visible={addModal} update={hide_modal}></Add_ticket_modal>
            <div className="tickets-top">
                <span className="tickets-title">Tickets list</span>
            </div>
            <div className="tickets-bottom">
                <div className="tickets-panel">
                    <div className="ticket-add-wrapper">
                        <button className="ticket-add-btn" onClick={show_modal}>+ Add new ticket</button>
                    </div>
                    <img className="blob-tright" src={BlobTRight} alt="Not found"></img>
                    {
                        tickets.length === 0 ?
                        (
                            <div className="tickets-content" style={{display: "flex", flexDirection: "row"}}>
                                <img className="tickets-noresults" src={noresult} alt="No Image"></img> 
                            </div>
                        ) : (
                            <div className="tickets-content"> {
                                tickets.map((e) => {
                                    let el = <Ticket id ={e.name} key={e.name} name={e.name} desc={e.desc}></Ticket>
                                    tickets_map.push({id: e.name, element: el})
                                    return el;
                                })
                            }
                            </div>
                        )
                    }
                    <div className="tickets-buttons">
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Tickets;
