import FloatModal from "../modalbase"
import index from "../../../index"

const Add_ticket_modal = (props) => {
    const form_submit_ticket = (e) => {
        e.preventDefault();
        const o_el = document.getElementById("select-field");
        const d_el = document.getElementById("desc-field");
        index.Client.emit("ticket-submit", {table: o_el.options[o_el.selectedIndex].text, desc: d_el.value})
        index.Client.on("ticket-exists", ({msg}) => {
            window.confirm(msg)
        })
        index.Client.on("ticket-close-modal", () => {
            props.update()
            d_el.value = ""
        })
    }
    const tables = []
    for (let i = 0; i <= 30; i++) {
        tables.push(<option key={i} value={i}> Table {i} </option>)
    }
    return (
        <FloatModal {...props}>
            <div class="add-ticket-wrapper">
                <p>New ticket information</p>
                <form action="" autoComplete="false" onSubmit={form_submit_ticket}>
                    <select id="select-field" className="select-field">
                        {tables}
                    </select>
                    <textarea id="desc-field" type="text" maxLength={200} min={10} rows={10} placeholder="Description of the ticket..." style={{resize:"none"}} required></textarea>
                    <input id="submit-field" type="submit" className="form-submit-btn" value="SUBMIT"></input>
                </form>
            </div>
        </FloatModal>
    )
}

export default Add_ticket_modal
